    name
    __typename
  }
  genres {
    id
    slug
    name
    __typename
  }
  country {
    name
    __typename
  }
  gallery {
    type
    photoPath
    backdrop
    __typename
  }
  quality {
    type
    language
    __typename
  }
  __typename
}

query movie_slug($slug: String) {
  show(slug: $slug, type: "movie") {
    ...MovieFields
    __typename
  }
}
